sysauto_script.script:
var count = 0;
var q = 'u_list_for_quo=NULL^ORu_list_for_quoISEMPTY';
var skkl = new GlideRecord('x_saghs_jp_qtm_ii_shikakari_list');
skkl.addEncodedQuery(q);
skkl.query();

while (skkl.next()) {
	var qtm = new GlideRecord('x_saghs_jp_qtm_quotation');
	qtm.addQuery('u_main_notification',skkl.sys_id);
	qtm.query();

	if (qtm.next()) {
		skkl.u_list_for_quo = qtm.sys_id;
		skkl.update();
		count++;
	}
}

gs.info(count+" notification records updated");
